import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  template: `
    <app-svg-map (countryClicked)="handleCountryClick($event)"></app-svg-map>
    <div class="selected-country" *ngIf="selectedCountry">
      Selected Country: {{ selectedCountry }}
    </div>
  `,
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  selectedCountry: string | null = null;

  handleCountryClick(countryName: string): void {
    console.log('Received country click event in parent:', countryName);
    this.selectedCountry = countryName; // Update selected country
  }
}
